import java.time.LocalDate


@RequiresApi(Build.VERSION_CODES.O)
@Composable
fun SearchResult(livro: Livro) {
    Card(
        modifier = Modifier
            .padding(4.dp)
            .background(
                contentColorFor(backgroundColor = Color.Black)
            ),
        shape = RoundedCornerShape(0.dp)
    ) {
        Row(
            modifier = Modifier
                .padding(2.dp)
                .fillMaxWidth()
                .background(color = Color.Black)
        ) {
            Livros(livro.capaUrl)

            Spacer(modifier = Modifier.width(4.dp))

            Column(
                modifier = Modifier
                    .padding(2.dp)
                    .background(color = Color.Black)
            ) {
                Text(
                    text = livro.titulo,
                    color = Color.White,
                    fontSize = 16.sp
                )
                Spacer(modifier = Modifier.height(4.dp))
                Text(
                    text = "${livro.autores}",
                    color = Color.White,
                    fontStyle = FontStyle.Italic
                )
                Spacer(modifier = Modifier.height(4.dp))
                Text(
                    text = "${livro.anoDePublicacao.year}",
                    color = Color.White
                )
            }
        }
    }
}

@RequiresApi(Build.VERSION_CODES.O)
@Preview(showBackground = true)
@Composable
fun SearchResultPreview() {
    var livro = Livro(
        titulo = "JavaScript: O Guia Definitivo",
        anoDePublicacao = LocalDate.of(2021,5,15),
        isbn = "9788575225631",
        numeroDePagina = 1096,
        idioma = "Português",
        sinopse =
        "Considerado a bíblia do JavaScript, este livro cobre desde os conceitos básicos até os mais avançados, sendo uma leitura essencial para desenvolvedores que desejam dominar a linguagem e suas complexidades.",
        editora = "Alta Books",
        generos = listOf("Programação", "JavaScript"),
        autores = listOf("David Flanagan"),
        capaUrl = "data:image/jpeg;base64,"
    )

    SearchResult(livro)
}
